package assignment.apr5ArrayConstructor;public class Q4Inventory {	String pname;	int qty,price;	Q4Inventory(){		pname="";		qty=0;		price=0;	}	Q4Inventory(String n,int q,int p) {		pname=n;		qty=q;		price=p;	}		public static void main(String[] args) {		Q4Inventory i=new Q4Inventory();		Q4Inventory i1=new Q4Inventory("AC",1,4000);		Q4Inventory i2=new Q4Inventory("Laptop",1,6000);		Q4Inventory i3=new Q4Inventory("TV",1,8000);				if((i1.price)>(i2.price) && (i2.price) >(i3.price)) {			System.out.print("\nAC is expensive");		}		else if((i2.price)>(i1.price) && (i2.price) >(i3.price)) {			System.out.print("\nLaptop is expensive");		}		else			System.out.print("\nTV is expensive");	}}