package dacclass.assignment.apr5ArrayConstructor;public class Q3Book {	String bookname,authorname;	int isbn,price,pages;	Q3Book(){		bookname="";		authorname="";		pages=0;		isbn=0;		price=0;		}	Q3Book(String n,String a,int p,int i,int pr){		bookname=n;		authorname=a;		pages=p;		isbn=i;		price=pr;	}	Q3Book(Q3Book e){		bookname=e.bookname;		authorname=e.authorname;		pages=e.pages;		isbn=e.isbn;		price=e.price;	}	public static void main(String[] args) {		new Q3Book();		Q3Book b1=new Q3Book("Java","Proton",150,30,600);		Q3Book b2=new Q3Book(b1);		System.out.println(" "+b1.bookname+" "+b1.authorname+" "+b1.pages+" "+b1.isbn+" "+b1.price);		System.out.println(" "+b2.bookname+" "+b2.authorname+" "+b2.pages+" "+b2.isbn+" "+b2.price);	}}